mapper.update:

	UPDATE
		d_game dg
	SET
		dg.status=#{status}
	WHERE
		dg.id=#{gameId}

update.foreach:
UPDATE 
	d_player_record dpr 

  	dpr.sign=#{item.sign},
  	dpr.identity_desc=#{item.identityDesc},
  	dpr.camp=#{item.camp},
  	dpr.is_life=#{item.isLife},
  	dpr.is_mute=#{item.isMute}
  	
  	,dpr.action=#{item.action}
  	
  	
  	,dpr.privilege=#{item.privilege}
  	
  	
  	,dpr.feedback=#{item.feedback}
  	
  	
  	,dpr.feedback=#{item.feedback}
  	
  	
  	,dpr.feedback=#{item.remark}
  	
  	
WHERE
  	dpr.player_id = #{item.playerId}
