mapper.update:

	UPDATE
		d_game dg
	SET
		dg.status=#{status}
		
		,dg.actual_start_date=NOW()
		
		
		,dg.end_date=NOW()
		,dg.final_result=#{finalResult}
		
	WHERE
		dg.id=#{gameId}

update.foreach:
UPDATE 
	d_player_record dpr 

  	dpr.sign=#{item.sign},
  	dpr.identity_desc=#{item.identityDesc},
  	dpr.is_sp=#{item.isSp},
  	dpr.camp=#{item.camp},
  	dpr.is_life=#{item.isLife},
  	dpr.is_mute=#{item.isMute},
  	dpr.position=#{item.position}
  	
  	,dpr.action=#{item.action}
  	
  	
  	,dpr.privilege=#{item.privilege}
  	
  	
  	,dpr.feedback=#{item.feedback}
  	
  	
  	,dpr.vote=#{item.vote}
  	
  	
  	,dpr.remark=#{item.remark}
  	
  	
WHERE
  	dpr.player_id = #{item.playerId}

mapper.update:

	UPDATE
		d_game dg
	SET
		dg.replay_essay_id=#{essayId}
	WHERE
		dg.id=#{gameId}
